package JavaIntro;

public class CasteVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int x = 25;
      double y = x; // automatically casting (widening)
      int z = (int) y; // manual casting(narrow)
      
      System.out.println(z);
      
      
      
      
	}

}
